const mainFooterButton=document.querySelector(".main-footer-button"),letterPopup=document.querySelector(".modal-letter "),letterClose=letterPopup.querySelector(".modal-close"),loginForm=letterPopup.querySelector(".letter-form"),loginLogin=letterPopup.querySelector(".input-name"),formEmail=letterPopup.querySelector(".input-email"),formMessage=letterPopup.querySelector(".letter-message");mainFooterButton.addEventListener("click",function(){letterPopup.classList.add("modal-show"),loginLogin.focus()}),letterClose.addEventListener("click",function(e){letterPopup.classList.remove("modal-show"),letterPopup.classList.remove("modal-error")}),loginForm.addEventListener("submit",function(e){loginLogin.value&&formEmail.value&&formMessage.value||(e.preventDefault(),letterPopup.classList.remove("modal-error"),letterPopup.offsetWidth=letterPopup.offsetWidth,letterPopup.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&letterPopup.classList.contains("modal-show")&&(e.preventDefault(),letterPopup.classList.remove("modal-show"),letterPopup.classList.remove("modal-error"))});
